class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        n = len(nums)
        max_or = 0

        # Try applying all k operations to each element
        for i in range(n):
            # Start with the original array's OR
            current_or = 0
            # Apply k operations to nums[i]
            current_val = nums[i] * (2 ** k)
            # Compute OR of all elements
            for j in range(n):
                if j == i:
                    current_or |= current_val
                else:
                    current_or |= nums[j]
            max_or = max(max_or, current_or)

        return max_or

