class Solution:
    def count(self, num1: str, num2: str, min_sum: int, max_sum: int) -> int:
        MOD = 10**9 + 7

        def digit_sum(n):
            return sum(int(d) for d in str(n))

        def count_good_integers(n):
            # Convert string to list of digits
            digits = [int(d) for d in n]
            length = len(digits)

            # Memoization for digit DP
            from functools import lru_cache

            @lru_cache(maxsize=None)
            def dp(pos, tight, current_sum, started):
                # pos: current position in the digit array
                # tight: whether we are still bounded by the original number
                # current_sum: current sum of digits
                # started: whether we have started placing non-zero digits

                if pos == length:
                    return 1 if current_sum >= min_sum and current_sum <= max_sum and started else 0

                result = 0
                limit = digits[pos] if tight else 9

                for digit in range(0, limit + 1):
                    new_tight = tight and (digit == limit)
                    new_started = started or (digit > 0)
                    new_sum = current_sum + digit

                    # If we haven't started and digit is 0, we continue without adding to sum
                    if not started and digit == 0:
                        result = (result + dp(pos + 1, new_tight, current_sum, False)) % MOD
                    else:
                        result = (result + dp(pos + 1, new_tight, new_sum, True)) % MOD

                return result

            return dp(0, True, 0, False)

        return (count_good_integers(num2) - count_good_integers(num1) + MOD) % MOD

