class Solution:
    def longestSemiRepetitiveSubstring(self, s: str) -> int:
        n = len(s)
        max_len = 0
        # Use sliding window with two pointers
        left = 0
        consecutive_pairs = 0  # Count of consecutive pairs of same digits

        for right in range(n):
            # Check if current character forms a consecutive pair with the previous one
            if right > 0 and s[right] == s[right - 1]:
                consecutive_pairs += 1

            # If we have more than one consecutive pair, shrink window from left
            while consecutive_pairs > 1:
                if left < n and s[left] == s[left + 1]:
                    consecutive_pairs -= 1
                left += 1

            # Update max length with current window size
            max_len = max(max_len, right - left + 1)

        return max_len

