class Solution:
    def punishmentNumber(self, n: int) -> int:
        total = 0
        for i in range(1, n + 1):
            square = i * i
            if self.canPartition(square, i):
                total += square
        return total

    def canPartition(self, num: int, target: int) -> bool:
        # Convert number to string to work with digits
        num_str = str(num)
        # Use DFS with backtracking to try all possible partitions
        def dfs(index, current_sum):
            # Base case: if we've processed all digits
            if index == len(num_str):
                return current_sum == target

            # Try all possible substrings starting from current index
            for j in range(index, len(num_str)):
                # Extract substring from index to j (inclusive)
                substring = num_str[index:j+1]
                # Skip if substring is "0" and not at the beginning (leading zero)
                if substring == "0" and index > 0:
                    continue
                # Convert substring to integer
                val = int(substring)
                # If adding this value would exceed target, skip
                if current_sum + val > target:
                    continue
                # Recursively check if the rest of the digits can form the remaining sum
                if dfs(j + 1, current_sum + val):
                    return True
            return False

        return dfs(0, 0)

