class Solution:
    def makeSmallestPalindrome(self, s: str) -> str:
        s = list(s)
        n = len(s)

        # Use two pointers from both ends moving towards the center
        for i in range(n // 2):
            # Compare characters at symmetric positions
            if s[i] != s[n - 1 - i]:
                # Choose the lexicographically smaller character
                s[i] = min(s[i], s[n - 1 - i])
                s[n - 1 - i] = min(s[i], s[n - 1 - i])

        return ''.join(s)

