class Solution:
    def removeTrailingZeros(self, num: str) -> str:
        # Start from the end of the string and find the last non-zero digit
        i = len(num) - 1
        while i >= 0 and num[i] == '0':
            i -= 1

        # Return the substring from the beginning to (and excluding) the first zero we found
        return num[:i + 1]

