class Solution:
    def canTraverseAllPairs(self, nums: List[int]) -> bool:
        n = len(nums)

        # Edge case: if only one element or empty array
        if n == 0:
            return True
        if n == 1:
            return True

        # If there's a 1, we can't traverse to any other index because gcd(1, x) = 1
        if 1 in nums:
            return False

        # Build a graph where nodes are indices, and edges exist if gcd(nums[i], nums[j]) > 1
        # Instead of building the full graph (which is O(n^2)), we use a union-find or factor-based approach

        # We'll use union-find (disjoint set union) to group indices that are connected via common factors
        parent = list(range(n))

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            px, py = find(x), find(y)
            if px != py:
                parent[px] = py

        # For each number, find its prime factors
        # For each prime factor, we union all indices that have that factor
        prime_to_indices = defaultdict(list)

        for i, num in enumerate(nums):
            # Find all prime factors of num
            temp = num
            d = 2
            while d * d <= temp:
                if temp % d == 0:
                    # d is a prime factor
                    prime_to_indices[d].append(i)
                    while temp % d == 0:
                        temp //= d
                d += 1
            # If temp > 1, then it's a prime factor
            if temp > 1:
                prime_to_indices[temp].append(i)

        # Union all indices that share a common prime factor
        for prime, indices in prime_to_indices.items():
            for i in range(1, len(indices)):
                union(indices[0], indices[i])

        # Check if all indices are in the same connected component
        root = find(0)
        for i in range(1, n):
            if find(i) != root:
                return False

        return True

