            to_pos = moveTo[i]

            # If the source position is occupied, remove it
            if from_pos in occupied:
                occupied.remove(from_pos)
                # Add the destination position
                occupied.add(to_pos)

        # Return sorted list of occupied positions
        return sorted(list(occupied))

