from typing import List

class Solution:
    def specialPerm(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        # Precompute divisibility relationships
        # For each pair (i, j), check if nums[i] % nums[j] == 0 or nums[j] % nums[i] == 0
        # We'll use a bitmask to represent which elements have been used
        @lru_cache(maxsize=None)
        def dp(mask, last_idx):
            # mask: bitmask representing which elements have been used
            # last_idx: index of the last element in the current permutation

            # If all elements are used, we have a valid permutation
            if mask == (1 << n) - 1:
                return 1

            total = 0
            # Try to add a new element at the end
            for i in range(n):
                # Skip if already used
                if mask & (1 << i):
                    continue

                # Check if nums[i] is divisible by last_idx or vice versa
                # If last_idx is -1, it means this is the first element
                if last_idx == -1:
                    # First element can be any number
                    total = (total + dp(mask | (1 << i), i)) % MOD
                else:
                    # Check divisibility condition
                    if nums[i] % nums[last_idx] == 0 or nums[last_idx] % nums[i] == 0:
                        total = (total + dp(mask | (1 << i), i)) % MOD

            return total

        return dp(0, -1)

