class Solution:
    def maximumJumps(self, nums: List[int], target: int) -> int:
        n = len(nums)
        # dp[i] represents the maximum number of jumps to reach index i
        dp = [-1] * n
        dp[0] = 0  # We start at index 0 with 0 jumps

        for i in range(1, n):
            for j in range(i):
                # Check if we can jump from j to i
                if dp[j] != -1 and -target <= nums[i] - nums[j] <= target:
                    if dp[i] == -1:
                        dp[i] = dp[j] + 1
                    else:
                        dp[i] = max(dp[i], dp[j] + 1)

        return dp[n - 1]

