from collections import Counter

class Solution:
    def countCompleteSubarrays(self, nums: List[int]) -> int:
        total_distinct = len(set(nums))
        count = 0

        # Iterate over all possible subarrays
        for i in range(len(nums)):
            seen = set()
            for j in range(i, len(nums)):
                seen.add(nums[j])
                # If we have collected all distinct elements, all subarrays from i to j and beyond are complete
                if len(seen) == total_distinct:
                    # All subarrays starting at i and ending at j, j+1, ..., len(nums)-1 are complete
                    count += len(nums) - j
                    break

        return count

