class Solution:
    def longestAlternatingSubarray(self, nums: List[int], threshold: int) -> int:
        max_length = 0
        n = len(nums)

        # Iterate through all possible starting indices
        for l in range(n):
            # Check if the starting element is even and within threshold
            if nums[l] % 2 == 1 or nums[l] > threshold:
                continue

            # Start a subarray from index l
            current_length = 1
            # Extend the subarray as long as conditions are met
            for r in range(l + 1, n):
                # Check if the current element is within threshold
                if nums[r] > threshold:
                    break

                # Check if the parity alternates
                if nums[r] % 2 == nums[r - 1] % 2:
                    break

                current_length += 1

            max_length = max(max_length, current_length)

        return max_length

