class Solution:
    def numberOfGoodSubarraySplits(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        # If there's no 1 in the array, no valid split exists
        if 1 not in nums:
            return 0

        # Find all positions of 1s
        ones = []
        for i, num in enumerate(nums):
            if num == 1:
                ones.append(i)

        # The number of ways to split is the product of distances between consecutive 1s
        # For example, if 1s are at positions [i1, i2, i3], then ways = (i2-i1) * (i3-i2)
        result = 1
        for i in range(1, len(ones)):
            distance = ones[i] - ones[i-1]
            result = (result * distance) % MOD

        return result

