class Solution:
    def maxNonDecreasingLength(self, nums1: List[int], nums2: List[int]) -> int:
        n = len(nums1)
        # dp[i][0] represents the length of the longest non-decreasing subarray ending at index i if we choose nums1[i]
        # dp[i][1] represents the length of the longest non-decreasing subarray ending at index i if we choose nums2[i]
        dp = [[0, 0] for _ in range(n)]

        # Base case: first element
        dp[0][0] = 1
        dp[0][1] = 1

        max_length = 1

        for i in range(1, n):
            # Option 1: choose nums1[i]
            # We can extend a non-decreasing subarray ending at i-1 if nums1[i] >= the last element of that subarray
            # Check if we can extend from nums1[i-1]
            if nums1[i] >= nums1[i-1]:
                dp[i][0] = dp[i-1][0] + 1
            else:
                dp[i][0] = 1

            # Check if we can extend from nums2[i-1]
            if nums1[i] >= nums2[i-1]:
                dp[i][0] = max(dp[i][0], dp[i-1][1] + 1)
            else:
                dp[i][0] = max(dp[i][0], 1)

            # Option 2: choose nums2[i]
            # Check if we can extend from nums1[i-1]
            if nums2[i] >= nums1[i-1]:
                dp[i][1] = dp[i-1][0] + 1
            else:
                dp[i][1] = 1

            # Check if we can extend from nums2[i-1]
            if nums2[i] >= nums2[i-1]:
                dp[i][1] = max(dp[i][1], dp[i-1][1] + 1)
            else:
                dp[i][1] = max(dp[i][1], 1)

            max_length = max(max_length, dp[i][0], dp[i][1])

        return max_length

