class Solution:
    def minimumChanges(self, s: str, k: int) -> int:
        n = len(s)

        # Precompute the cost to make a substring into a semi-palindrome
        # cost[i][j] = minimum changes to make s[i:j+1] into a semi-palindrome
        cost = [[0] * n for _ in range(n)]

        for i in range(n):
            for j in range(i, n):
                if j == i:
                    # Single character - not a semi-palindrome (len=1, no valid d)
                    cost[i][j] = float('inf')
                else:
                    # Try all possible divisors d where 1 <= d < len and len % d == 0
                    length = j - i + 1
                    min_changes = float('inf')

                    # Find all divisors of length
                    for d in range(1, length):
                        if length % d == 0:
                            # Group indices by modulo d
                            groups = [[] for _ in range(d)]
                            for idx in range(i, j + 1):
                                groups[idx % d].append(s[idx])

                            # For each group, check if it's a palindrome
                            changes = 0
                            for group in groups:
                                if len(group) > 1:
                                    # Check if group is palindrome
                                    group_str = ''.join(group)
                                    mid = len(group_str) // 2
                                    for p in range(mid):
                                        if group_str[p] != group_str[-p - 1]:
                                            changes += 1
                            min_changes = min(min_changes, changes)

                    cost[i][j] = min_changes

        # Now use dynamic programming to partition s into k substrings
        # dp[i][j] = minimum cost to partition s[0:i+1] into j substrings
        dp = [[float('inf')] * (k + 1) for _ in range(n)]

        # Base case: 0 cost to partition empty string into 0 substrings
        dp[0][0] = 0

        for i in range(n):
            for j in range(1, min(k, i + 1) + 1):
                # Try all possible last substring ending at i
                for prev in range(i):
                    # Partition s[0:prev+1] into j-1 substrings
                    # and s[prev+1:i+1] as the j-th substring
                    if dp[prev][j - 1] != float('inf'):
                        total_cost = dp[prev][j - 1] + cost[prev + 1][i]
                        dp[i][j] = min(dp[i][j], total_cost)

        return dp[n - 1][k]

