class Solution:
    def countSteppingNumbers(self, low: str, high: str) -> int:
        MOD = 10**9 + 7

        def count_up_to(n):
            if not n:
                return 0
            digits = [int(d) for d in n]
            n_len = len(digits)

            # dp[i][prev][tight][started] = count of valid numbers up to i-th digit
            # prev: previous digit (0-9), -1 means no previous digit (start)
            # tight: whether we are still bounded by n
            # started: whether we have started (to avoid leading zeros)
            from functools import lru_cache

            @lru_cache(maxsize=None)
            def dfs(pos, prev, tight, started):
                if pos == n_len:
                    return 1 if started else 0

                limit = digits[pos] if tight else 9
                result = 0

                for d in range(0, limit + 1):
                    new_tight = tight and (d == limit)
                    new_started = started or (d > 0)

                    # If we haven't started and d is 0, we skip (leading zero)
                    if not new_started and d == 0:
                        result += dfs(pos + 1, prev, new_tight, new_started)
                        continue

                    # If we have started, check stepping condition
                    if started:
                        if abs(d - prev) != 1:
                            continue
                    # If we are at the first digit (no prev), we can place any digit
                    else:
                        # This is the first digit, so no stepping condition
                        pass

                    result += dfs(pos + 1, d, new_tight, new_started)

                return result % MOD

            return dfs(0, -1, True, False)

        return (count_up_to(high) - count_up_to(low - 1)) % MOD

