class Solution:
    def numberOfBeautifulIntegers(self, low: int, high: int, k: int) -> int:
        def count_beautiful(n):
            if n < 10:
                return 0

            digits = []
            temp = n
            while temp:
                digits.append(temp % 10)
                temp //= 10

            # Count even and odd digits
            even_count = 0
            odd_count = 0
            for d in digits:
                if d % 2 == 0:
                    even_count += 1
                else:
                    odd_count += 1

            # Check if even and odd counts are equal
            if even_count != odd_count:
                return 0

            # Check if divisible by k
            return 1 if n % k == 0 else 0

        return count_beautiful(high) - count_beautiful(low - 1)

