class Solution:
    def canMakeSubsequence(self, str1: str, str2: str) -> bool:
        i = 0  # pointer for str1
        j = 0  # pointer for str2

        while i < len(str1) and j < len(str2):
            # Check if current character in str1 matches current character in str2
            # Either directly matches, or after incrementing str1[i] it matches str2[j]
            if str1[i] == str2[j] or \
               (chr((ord(str1[i]) - ord('a') + 1) % 26 + ord('a')) == str2[j]):
                j += 1  # move to next character in str2
            i += 1  # always move to next character in str1

        # If we've matched all characters in str2, return True
        return j == len(str2)

