class Solution:
    def maximumSum(self, nums: List[int]) -> int:
        n = len(nums)
        # Initialize the maximum sum to the largest single element
        max_sum = max(nums)

        # For each pair of indices (i, j) where i < j
        for i in range(n):
            for j in range(i + 1, n):
                # Check if the product of nums[i] and nums[j] is a perfect square
                if self.isPerfectSquare(nums[i] * nums[j]):
                    # Update max_sum if this pair gives a larger sum
                    max_sum = max(max_sum, nums[i] + nums[j])

        # Check for larger subsets (triplets, etc.)
        # For each triplet (i, j, k) where i < j < k
        for i in range(n):
            for j in range(i + 1, n):
                for k in range(j + 1, n):
                    # Check if the product of every pair among (i, j, k) is a perfect square
                    if (self.isPerfectSquare(nums[i] * nums[j]) and
                        self.isPerfectSquare(nums[i] * nums[k]) and
                        self.isPerfectSquare(nums[j] * nums[k])):
                        max_sum = max(max_sum, nums[i] + nums[j] + nums[k])

        return max_sum

    def isPerfectSquare(self, x: int) -> bool:
        if x <= 0:
            return False
        # Find the integer square root
        root = int(x ** 0.5)
        return root * root == x

