class Solution:
    def maxSubarrays(self, nums: List[int]) -> int:
        total_and = nums[0]
        for num in nums[1:]:
            total_and &= num

        if total_and == 0:
            # If the total AND of the entire array is 0, we can potentially split into more subarrays
            # We want to maximize the number of subarrays, so we try to make each subarray have score 0
            count = 0
            current_and = nums[0]
            for num in nums:
                current_and &= num
                if current_and == 0:
                    count += 1
                    current_and = num  # Start a new subarray from the next element
            return count
        else:
            # If the total AND is not zero, then every subarray will have a score at least total_and
            # So we cannot split into more than one subarray
            return 1

