class Solution:
    def minLengthAfterRemovals(self, nums: List[int]) -> int:
        n = len(nums)
        if n <= 2:
            return n

        # Count frequency of each element
        freq = {}
        for num in nums:
            freq[num] = freq.get(num, 0) + 1

        # Find the maximum frequency
        max_freq = max(freq.values())

        # If the maximum frequency is less than or equal to n - max_freq,
        # then we can pair all elements and reduce the array to empty
        if max_freq <= n - max_freq:
            return 0
        else:
            # Otherwise, the remaining elements will be the difference
            # between the maximum frequency and the rest
            return max_freq - (n - max_freq)

