        n = len(s)
        beautiful_substrings = []

        # Generate all substrings and check if they have exactly k ones
        for i in range(n):
            ones = 0
            for j in range(i, n):
                if s[j] == '1':
                    ones += 1
                if ones == k:
                    beautiful_substrings.append(s[i:j+1])
                    break

        # If no beautiful substring found, return empty string
        if not beautiful_substrings:
            return ""

        # Find the shortest length
        min_len = min(len(sub) for sub in beautiful_substrings)

        # Filter substrings with the shortest length and return lexicographically smallest
        candidates = [sub for sub in beautiful_substrings if len(sub) == min_len]
        return min(candidates)

