class Solution:
    def maxSum(self, nums: List[int], k: int) -> int:
        MOD = 10**9 + 7

        # Count the frequency of each bit in the array
        # We only care about bits up to 31 since nums[i] <= 10^9
        bit_count = [0] * 32

        for num in nums:
            for bit in range(32):
                if num & (1 << bit):
                    bit_count[bit] += 1

        # Build the result array by greedily assigning bits
        # For each of the k elements, we assign the highest available bits
        result = []
        for _ in range(k):
            num = 0
            for bit in range(31, -1, -1):
                if bit_count[bit] > 0:
                    num |= (1 << bit)
                    bit_count[bit] -= 1
            result.append(num)

        # Calculate the sum of squares
        total_sum = 0
        for num in result:
            total_sum = (total_sum + num * num) % MOD

        return total_sum

