class Solution:
    def lastVisitedIntegers(self, words: List[str]) -> List[int]:
        nums = []
        result = []
        k = 0

        for word in words:
            if word == "prev":
                k += 1
                if k > len(nums):
                    result.append(-1)
                else:
                    # Get the (k-1)th index from the reverse of nums
                    result.append(nums[-k])
            else:
                # Convert string to integer and append to nums
                nums.append(int(word))
                k = 0  # Reset k when we see a number

        return result

