class Solution:
    def minSum(self, nums1: List[int], nums2: List[int]) -> int:
        sum1 = sum(nums1)
        sum2 = sum(nums2)

        zeros1 = nums1.count(0)
        zeros2 = nums2.count(0)

        # If there are no zeros in either array
        if zeros1 == 0 and zeros2 == 0:
            return sum1 if sum1 == sum2 else -1

        # If nums1 has zeros, we can increase sum1 to at least sum1 + zeros1
        # If nums2 has zeros, we can increase sum2 to at least sum2 + zeros2
        min_sum1 = sum1 + zeros1 if zeros1 > 0 else float('inf')
        min_sum2 = sum2 + zeros2 if zeros2 > 0 else float('inf')

        # If both arrays have no zeros, we can't change the sum
        if min_sum1 == float('inf') and min_sum2 == float('inf'):
            return sum1 if sum1 == sum2 else -1

        # If one array has no zeros, the other must match its sum
        if min_sum1 == float('inf'):
            return min_sum2 if min_sum2 >= sum1 else -1
        if min_sum2 == float('inf'):
            return min_sum1 if min_sum1 >= sum2 else -1

        # Both arrays can be increased, so we take the maximum of the minimum possible sums
        return max(min_sum1, min_sum2)

