    def findKOr(self, nums: List[int], k: int) -> int:
        result = 0
        # Check each bit position from 0 to 31 (since nums[i] < 2^31)
        for bit in range(32):
            # Count how many numbers in nums have this bit set
            count = 0
            for num in nums:
                if (num >> bit) & 1:
                    count += 1
            # If at least k numbers have this bit set, include it in the result
            if count >= k:
                result |= (1 << bit)
        return result

