class Solution:
    def distributeCandies(self, n: int, limit: int) -> int:
        count = 0
        # Iterate over all possible distributions of candies to 3 children
        for a in range(limit + 1):
            for b in range(limit + 1):
                c = n - a - b
                # Check if the distribution is valid (non-negative and within limit)
                if c >= 0 and c <= limit:
                    count += 1
        return count

