class Solution:
    def findMinimumOperations(self, s1: str, s2: str, s3: str) -> int:
        # Check if the first characters are the same
        if s1[0] != s2[0] or s2[0] != s3[0]:
            return -1

        # If all strings are already equal, return 0
        if s1 == s2 == s3:
            return 0

        # Find the longest common prefix
        min_len = min(len(s1), len(s2), len(s3))
        common_prefix_len = 0

        for i in range(min_len):
            if s1[i] == s2[i] == s3[i]:
                common_prefix_len += 1
            else:
                break

        # If no common prefix, return -1
        if common_prefix_len == 0:
            return -1

        # Calculate the number of operations needed
        # We need to remove characters from the end of each string
        # until they all have the same prefix
        ops = (len(s1) - common_prefix_len) + (len(s2) - common_prefix_len) + (len(s3) - common_prefix_len)

        return ops

