class Solution:
    def numberOfGoodPartitions(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        n = len(nums)
        if n == 0:
            return 0

        # Find the first and last occurrence of each number
        first_occurrence = {}
        last_occurrence = {}

        for i, num in enumerate(nums):
            if num not in first_occurrence:
                first_occurrence[num] = i
            last_occurrence[num] = i

        # We'll use a greedy approach to find the boundaries of good partitions
        # We traverse the array and keep track of the current "span" of a partition
        # A partition must include all occurrences of a number that appears in it

        # We'll use a variable to track the current rightmost boundary of the span
        rightmost = 0
        partitions = 0

        for i in range(n):
            # Extend the current span to include the last occurrence of nums[i]
            rightmost = max(rightmost, last_occurrence[nums[i]])

            # If we've reached the end of the current span, we can form a new partition
            if i == rightmost:
                partitions += 1

        return partitions % MOD

