class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        max_val = max(nums)
        count = 0
        n = len(nums)

        # For each possible starting index
        for i in range(n):
            freq = 0
            for j in range(i, n):
                if nums[j] == max_val:
                    freq += 1
                if freq >= k:
                    # All subarrays starting at i and ending at j or beyond will also satisfy the condition
                    count += n - j
                    break

        return count

