class Solution:
    def findMissingAndRepeatedValues(self, grid: List[List[int]]) -> List[int]:
        n = len(grid)
        total_elements = n * n
        # Flatten the grid into a list
        flat_grid = []
        for row in grid:
            for val in row:
                flat_grid.append(val)

        # Count frequency of each value
        freq = {}
        for val in flat_grid:
            freq[val] = freq.get(val, 0) + 1

        # Find the repeated value (appears twice)
        repeated = 0
        for val, count in freq.items():
            if count == 2:
                repeated = val

        # Find the missing value (appears 0 times)
        missing = 0
        for val in range(1, total_elements + 1):
            if val not in freq:
                missing = val
                break

        return [repeated, missing]

