class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        # Build a graph of character transformations
        n = 26
        # graph[i][j] represents the minimum cost to transform character i to character j
        graph = [[float('inf')] * n for _ in range(n)]

        # Initialize diagonal to 0 (no cost to stay same)
        for i in range(n):
            graph[i][i] = 0

        # Fill the graph with transformation costs
        for o, c, cst in zip(original, changed, cost):
            o_idx = ord(o) - ord('a')
            c_idx = ord(c) - ord('a')
            graph[o_idx][c_idx] = min(graph[o_idx][c_idx], cst)

        # Use Floyd-Warshall to find shortest paths between all pairs of characters
        for k in range(n):
            for i in range(n):
                for j in range(n):
                    if graph[i][k] != float('inf') and graph[k][j] != float('inf'):
                        graph[i][j] = min(graph[i][j], graph[i][k] + graph[k][j])

        total_cost = 0
        for i in range(len(source)):
            if source[i] != target[i]:
                s_idx = ord(source[i]) - ord('a')
                t_idx = ord(target[i]) - ord('a')
                if graph[s_idx][t_idx] == float('inf'):
                    return -1
                total_cost += graph[s_idx][t_idx]

        return total_cost

