class Solution:
    def minimumOperationsToMakeEqual(self, x: int, y: int) -> int:
        if x == y:
            return 0

        # If x < y, we can only increment x
        if x < y:
            return y - x

        # If x > y, we can use a BFS approach to find the minimum operations
        from collections import deque

        queue = deque([(x, 0)])  # (current_value, operations_count)
        visited = {x}

        while queue:
            current, ops = queue.popleft()

            # Try all possible operations
            next_vals = []

            # Operation 1: Divide by 11 if divisible
            if current % 11 == 0:
                next_vals.append(current // 11)

            # Operation 2: Divide by 5 if divisible
            if current % 5 == 0:
                next_vals.append(current // 5)

            # Operation 3: Decrement by 1
            next_vals.append(current - 1)

            # Operation 4: Increment by 1
            next_vals.append(current + 1)

            for next_val in next_vals:
                if next_val == y:
                    return ops + 1

                if next_val > 0 and next_val not in visited:
                    visited.add(next_val)
                    queue.append((next_val, ops + 1))

        return -1  # Should never reach here given constraints

