class Solution:
    def minimumArrayLength(self, nums: List[int]) -> int:
        min_val = min(nums)
        # If all numbers are equal, we can reduce to just one element
        if min_val == max(nums):
            return 1

        # Count how many times the minimum value appears
        count_min = nums.count(min_val)

        # If the minimum value appears at least twice, we can reduce the array to just one element
        # Otherwise, we need at least two elements (the minimum value and one other)
        if count_min >= 2:
            return 1
        else:
            return 2

