class Solution:
    def minOperations(self, nums: List[int], k: int) -> int:
        current_xor = 0
        for num in nums:
            current_xor ^= num

        # The difference between current_xor and k gives us the bits that need to be flipped
        diff = current_xor ^ k

        # Count the number of set bits in diff
        result = 0
        while diff:
            result += diff & 1
            diff >>= 1

        return result

