class Solution:
    def incremovableSubarrayCount(self, nums: List[int]) -> int:
        n = len(nums)
        count = 0

        # Try every possible subarray to remove
        for i in range(n):
            for j in range(i, n):
                # Remove subarray from i to j
                # Remaining elements are from 0 to i-1 and from j+1 to n-1
                left_part = nums[:i]
                right_part = nums[j+1:]

                # Check if the resulting array is strictly increasing
                if not left_part:
                    # Only right part remains
                    if not right_part or self.isStrictlyIncreasing(right_part):
                        count += 1
                elif not right_part:
                    # Only left part remains
                    if self.isStrictlyIncreasing(left_part):
                        count += 1
                else:
                    # Both parts remain
                    # Check if the last element of left part < first element of right part
                    if left_part[-1] < right_part[0]:
                        # Combine both parts and check if strictly increasing
                        combined = left_part + right_part
                        if self.isStrictlyIncreasing(combined):
                            count += 1

        return count

    def isStrictlyIncreasing(self, arr):
        for i in range(1, len(arr)):
            if arr[i] <= arr[i-1]:
                return False
        return True

