class Solution:
    def maxSelectedElements(self, nums: List[int]) -> int:
        nums.sort()
        n = len(nums)
        # dp[i] represents the maximum number of consecutive elements we can select ending at index i
        dp = [1] * n

        for i in range(1, n):
            # We can always select just nums[i] itself
            dp[i] = 1

            # Check if we can extend a sequence ending at j (j < i)
            # We can extend if nums[i] - nums[j] <= 2 because we can increase any element by at most 1
            # So if nums[i] - nums[j] <= 2, then nums[j] can be increased to nums[j] + 1, and we can form a consecutive sequence
            for j in range(i):
                if nums[i] - nums[j] <= 2:
                    dp[i] = max(dp[i], dp[j] + 1)

        return max(dp)

