class Solution:
    def lastNonEmptyString(self, s: str) -> str:
        from collections import Counter

        # Count frequency of each character
        char_count = Counter(s)

        # Find the minimum frequency
        min_freq = min(char_count.values())

        # The last non-empty string will contain only characters that appear exactly min_freq times
        # We need to preserve the order of characters as they appear in the original string
        result = []
        for char in s:
            # Only add character if its frequency is equal to min_freq
            if char_count[char] == min_freq:
                result.append(char)

        return "".join(result)

