def solve():
    N, M = map(int, input().split())
    sets = []
    for _ in range(N):
        A = int(input())
        s = list(map(int, input().split()))
        sets.append(set(s))

    # If already contains both 1 and M
    if 1 in sets[0] and M in sets[0]:
        return 0

    # Build graph of sets that share common elements
    # Each node is a set, and there's an edge between two sets if they share at least one element
    # We want to know if we can merge sets to get a set containing both 1 and M

    # Use union-find to group sets that can be merged
    parent = list(range(N))

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        px, py = find(x), find(y)
        if px != py:
            parent[px] = py

    # For each element from 1 to M, find all sets that contain it
    element_to_sets = defaultdict(list)
    for i, s in enumerate(sets):
        for elem in s:
            element_to_sets[elem].append(i)

    # For each element, if there are multiple sets containing it, merge those sets
    for elem, indices in element_to_sets.items():
        if len(indices) > 1:
            for j in range(1, len(indices)):
                union(indices[0], indices[j])

    # Now, check if there's a connected component that contains both 1 and M
    # But we need to check if there's a path from a set containing 1 to a set containing M
    # We can do this by checking if there's a set containing 1 and a set containing M in the same component

    # Find all sets that contain 1
    sets_with_1 = []
    sets_with_M = []

    for i, s in enumerate(sets):
        if 1 in s:
            sets_with_1.append(i)
        if M in s:
            sets_with_M.append(i)

    # Check if there's a common component
    component_of_1 = set()
    component_of_M = set()

    for i in sets_with_1:
        component_of_1.add(find(i))

    for i in sets_with_M:
        component_of_M.add(find(i))

    # If no common component, impossible
    if not component_of_1.intersection(component_of_M):
        return -1

    # Now, we need to find the minimum number of operations to merge all sets in the component
    # The number of operations is (number of sets in the component - 1)
    # But we need to find the component that contains both 1 and M

    # Find the component that contains both 1 and M
    target_component = None
    for comp in component_of_1.intersection(component_of_M):
        # Count the number of sets in this component
        count = 0
        for i in range(N):
            if find(i) == comp:
                count += 1
        if count > 0:
            target_component = comp
            break

    # The number of operations is (number of sets in the component - 1)
    if target_component is not None:
        count = 0
        for i in range(N):
            if find(i) == target_component:
                count += 1
        return count - 1
    else:
        return -1

