def solve(s):
    n = len(s)
    # dp[i][j] = number of ways to make a valid parenthesis string for s[0:i] 
    # with j open parentheses at the end
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(n):
        for j in range(n + 1):
            if dp[i][j] == 0:
                continue
            c = s[i]
            if c == '(':
                dp[i+1][j+1] = (dp[i+1][j+1] + dp[i][j]) % MOD
            elif c == ')':
                if j > 0:
                    dp[i+1][j-1] = (dp[i+1][j-1] + dp[i][j]) % MOD
            else:  # c == '?'
                dp[i+1][j+1] = (dp[i+1][j+1] + dp[i][j]) % MOD
                if j > 0:
                    dp[i+1][j-1] = (dp[i+1][j-1] + dp[i][j]) % MOD

    # The answer is the number of ways to have 0 open parentheses at the end
    return dp[n][0]

