    for a, b in edges:
        graph[a].append(b)

    # Find all possible strongest programmers
    # A person is the strongest if they are stronger than everyone else
    # We can use the transitive closure to find all reachability

    # Use Floyd-Warshall to compute transitive closure
    # dist[i][j] = True if i is stronger than j
    dist = [[False] * (N+1) for _ in range(N+1)]

