        for j in range(4):
            row = grid[i*4 + j]
            poly.append(list(row))
        polyominoes.append(poly)

    # Function to rotate a polyomino 90 degrees clockwise
    def rotate(poly):
        n = len(poly)
        rotated = [['' for _ in range(n)] for _ in range(n)]
        for i in range(n):
            for j in range(n):
                rotated[j][n-1-i] = poly[i][j]
        return rotated

    # Function to translate a polyomino by (dx, dy)
    def translate(poly, dx, dy):
        n = len(poly)
        translated = [['' for _ in range(n)] for _ in range(n)]
        for i in range(n):
            for j in range(n):
                if poly[i][j] == '#':
                    ni, nj = i + dy, j + dx
                    if 0 <= ni < n and 0 <= nj < n:
                        translated[ni][nj] = '#'
        return translated

    # Function to check if two polyominoes overlap
    def overlap(poly1, poly2):
        n1 = len(poly1)
        n2 = len(poly2)
        for i in range(n1):
            for j in range(n1):
                if poly1[i][j] == '#':
                    for k in range(n2):
                        for l in range(n2):
                            if poly2[k][l] == '#' and (i == k and j == l):
                                return True
        return False

    # Function to check if a polyomino fits in the grid
    def fits(poly, grid):
        n = len(poly)
        for i in range(n):
            for j in range(n):
                if poly[i][j] == '#':
                    if i < 0 or i >= 4 or j < 0 or j >= 4:
                        return False
        return True

    # Function to check if a polyomino is valid (connected)
    def is_connected(poly):
        if not poly or not any(poly[i][j] == '#' for i in range(len(poly)) for j in range(len(poly))):
            return False
        visited = [[False for _ in range(len(poly))] for _ in range(len(poly))]
        stack = []
        for i in range(len(poly)):
            for j in range(len(poly)):
                if poly[i][j] == '#':
                    stack.append((i, j))
                    visited[i][j] = True
                    break
            if stack:
                break
        while stack:
            i, j = stack.pop()
            for di, dj in [(0,1), (0,-1), (1,0), (-1,0)]:
                ni, nj = i + di, j + dj
                if 0 <= ni < len(poly) and 0 <= nj < len(poly) and poly[ni][nj] == '#' and not visited[ni][nj]:
                    visited[ni][nj] = True
                    stack.append((ni, nj))
        return all(visited[i][j] for i in range(len(poly)) for j in range(len(poly)) if poly[i][j] == '#')

    # Function to check if a polyomino can be placed on the grid
    def can_place(poly, grid):
        n = len(poly)
        for i in range(n):
