    # Function to check if two polyominoes overlap
    def overlap(poly1, poly2):
        n1 = len(poly1)
        n2 = len(poly2)
        for i in range(n1):
            for j in range(n1):
                if poly1[i][j] == '#':
                    for k in range(n2):
                        for l in range(n2):
                            if poly2[k][l] == '#' and (i == k and j == l):
                                return True
        return False

    # Function to check if a polyomino fits in the grid
    def fits(poly, grid):
        n = len(poly)
        for i in range(n):
            for j in range(n):
                if poly[i][j] == '#':
                    if i < 0 or i >= 4 or j < 0 or j >= 4:
                        return False
        return True

    # Function to check if a polyomino is valid (connected)
    def is_connected(poly):
        if not poly or not any(poly[i][j] == '#' for i in range(len(poly)) for j in range(len(poly))):
            return False
        visited = [[False for _ in range(len(poly))] for _ in range(len(poly))]
        stack = []
        for i in range(len(poly)):
            for j in range(len(poly)):
                if poly[i][j] == '#':
                    stack.append((i, j))
                    visited[i][j] = True
                    break
            if stack:
                break
        while stack:
            i, j = stack.pop()
            for di, dj in [(0,1), (0,-1), (1,0), (-1,0)]:
                ni, nj = i + di, j + dj
                if 0 <= ni < len(poly) and 0 <= nj < len(poly) and poly[ni][nj] == '#' and not visited[ni][nj]:
                    visited[ni][nj] = True
                    stack.append((ni, nj))
        return all(visited[i][j] for i in range(len(poly)) for j in range(len(poly)) if poly[i][j] == '#')

    # Function to check if a polyomino can be placed on the grid
    def can_place(poly, grid):
        n = len(poly)
        for i in range(n):
            for j in range(n):
                if poly[i][j] == '#':
                    if i < 0 or i >= 4 or j < 0 or j >= 4:
                        return False
                    if grid[i][j] == '#':
                        return False
        return True

    # Function to place a polyomino on the grid and return the new grid
    def place(poly, grid, dx, dy):
        new_grid = [list(row) for row in grid]
        n = len(poly)
        for i in range(n):
            for j in range(n):
                if poly[i][j] == '#':
                    ni, nj = i + dy, j + dx
                    if 0 <= ni < 4 and 0 <= nj < 4:
                        new_grid[ni][nj] = '#'
        return new_grid

    # Function to check if the grid is completely filled
    def is_filled(grid):
        for i in range(4):
            for j in range(4):
                if grid[i][j] == '.':
                    return False
        return True

    # Try all possible rotations and translations of the polyominoes
    # We will use backtracking to try all combinations
    def backtrack(poly_idx, current_grid):
        if poly_idx == 3:
            return is_filled(current_grid)

        poly = polyominoes[poly_idx]
        n = len(poly)

        # Try all rotations (0 to 3)
        for rot in range(4):
            rotated = rotate(poly)
            for _ in range(rot):
                rotated = rotate(rotated)

            # Try all translations (dx, dy) such that the polyomino fits in the grid
            for dx in range(-3, 4):
                for dy in range(-3, 4):
                    # Check if the rotated polyomino fits in the grid
                    if can_place(rotated, current_grid):
                        new_grid = place(rotated, current_grid, dx, dy)
                        if backtrack(poly_idx + 1, new_grid):
                            return True

        return False

    # Check if the grid can be filled
    initial_grid = [['.' for _ in range(4)] for _ in range(4)]
    if backtrack(0, initial_grid):
        print("Yes")
