    # Calculate current total scores for each player
    current_scores = []
    for i in range(N):
        score = 0
        for j in range(M):
            if S[i][j] == 'o':
                score += A[j]
        score += i + 1  # bonus of i+1 points
        current_scores.append(score)

    # For each player, determine the minimum number of unsolved problems they need to solve
    results = []
    for i in range(N):
        # If player i is already ahead of all others, answer is 0
        if all(current_scores[j] < current_scores[i] for j in range(N) if j != i):
            results.append(0)
            continue

        # Otherwise, we need to find the minimum number of unsolved problems to exceed all others
        # We'll try solving problems one by one, starting with the highest-scoring unsolved problems
        unsolved_indices = []
        for j in range(M):
            if S[i][j] == 'x':
                unsolved_indices.append(j)

        # Sort unsolved problems by score in descending order
        unsolved_indices.sort(key=lambda x: A[x], reverse=True)

        # Try solving k problems (k from 1 to len(unsolved_indices))
        for k in range(1, len(unsolved_indices) + 1):
            new_score = current_scores[i] + sum(A[unsolved_indices[j]] for j in range(k))
            # Check if this new score exceeds all other players' scores
            if all(new_score > current_scores[j] for j in range(N)):
