from itertools import product
from collections import defaultdict
import sys

def solve():
    N, M = map(int, input().split())
    edges = []
    graph = defaultdict(list)

    for _ in range(M):
        u, v, w = map(int, input().split())
        edges.append((u, v, w))
        graph[u].append((v, w))

    # If there's no path from any vertex to any other vertex, then we can't visit all vertices
    # We need to check if the graph is strongly connected or if we can reach all vertices from some starting point

    # First, check if we can reach all vertices from any starting vertex
    # We'll use BFS/DFS to check reachability from each vertex
    def can_reach_all(start):
        visited = set()
        stack = [start]
        visited.add(start)

        while stack:
            node = stack.pop()
            for next_node, _ in graph[node]:
                if next_node not in visited:
                    visited.add(next_node)
                    stack.append(next_node)

        return len(visited) == N

    # Check if there's any vertex from which we can reach all other vertices
    # If not, then we cannot visit all vertices
    for i in range(1, N+1):
        if can_reach_all(i):
            break
    else:
        print("No")
        return

    # Now, we know that we can visit all vertices. We need to find the minimum total weight walk
    # Since N is small (<=20), we can use dynamic programming with bitmasks
    # State: dp[mask][last] = minimum total weight to visit all vertices in mask, ending at vertex 'last'
    # We need to find the minimum over all possible masks and last vertices

    # Initialize DP table
    INF = float('inf')
    dp = {}

    # Initialize with all single vertices
    for i in range(1, N+1):
        dp[(1 << (i-1)), i] = 0

    # Iterate over all possible masks
    for mask in range(1, 1 << N):
        for last in range(1, N+1):
            if (mask, last) not in dp or dp[(mask, last)] == INF:
                continue

            # Try to extend the walk to any neighbor
            for next_node, weight in graph[last]:
                new_mask = mask | (1 << (next_node-1))
                new_weight = dp[(mask, last)] + weight

                if (new_mask, next_node) not in dp or dp[(new_mask, next_node)] > new_weight:
                    dp[(new_mask, next_node)] = new_weight

    # Find the minimum total weight among all masks that include all vertices
    result = INF
    full_mask = (1 << N) - 1
    for last in range(1, N+1):
        if (full_mask, last) in dp:
            result = min(result, dp[(full_mask, last)])

    if result == INF:
        print("No")
    else:
        print(result)

solve()

