    """Check if the given file name is valid according to the provided conditions.

    Args:
    file_name (str): The file name to check.

    Returns:
    str: 'Yes' if the file name is valid, 'No' otherwise.
    """
    if file_name.count('.') != 1:
        return 'No'

    name, ext = file_name.split('.')

    if not name:
        return 'No'

    if not name[0].isalpha():
        return 'No'

    if ext not in ['txt', 'exe', 'dll']:
        return 'No'

    if sum(1 for char in name if char.isdigit()) > 3:
        return 'No'

    return 'Yes'

