def check_dict_case(d):
    """
    Given a dictionary, return True if all keys are strings in lower 
    case or all keys are strings in upper case, else return False.
    The function should return False is the given dictionary is empty.

    Parameters:
    d (dict): The input dictionary

    Returns:
    bool: True if all keys are strings in lower case or all keys are strings in upper case, else False
    """

    # Check if the dictionary is empty
    if not d:
        return False

    # Initialize a variable to store the first key
    first_key = list(d.keys())[0]

    # Check if all keys are strings
    if not isinstance(first_key, str):
        return False

    # Check if all keys are in lower case
    if first_key.islower():
        for key in d.keys():
            if not key.islower():
                return False
        return True

    # Check if all keys are in upper case
    elif first_key.isupper():
        for key in d.keys():
            if not key.isupper():
                return False
        return True

    # If neither of the above conditions is met
    else:
        return False

