def histogram(test):
    """
    Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}
    """
    # Split the input string
    letters = test.split()

    # Create an empty dictionary to store the counts
    count_dict = {}

    # Iterate through the list of letters
    for letter in letters:
        # If the letter is not in the dictionary, add it with a count of 1
        if letter not in count_dict:
            count_dict[letter] = 1
        # If the letter is already in the dictionary, increment its count
        else:
            count_dict[letter] += 1

    # Find the maximum count
    max_count = max(count_dict.values())

    # Create a list of letters with the maximum count
    max_letters = [key for key, value in count_dict.items() if value == max_count]

    # Return the list of letters with the maximum count
    return {max_letters[0]: max_count}

