from collections import deque

def minPath(grid, k):
    n = len(grid)
    visited = set()
    queue = deque([(0, 0, [grid[0][0]], 1)])  # (row, col, path, length)

    while queue:
        row, col, path, length = queue.popleft()
        if length == k:
            return path

        if (row, col) in visited:
            continue
        visited.add((row, col))

        for dr, dc in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
            new_row, new_col = row + dr, col + dc
            if 0 <= new_row < n and 0 <= new_col < n:
                new_path = path + [grid[new_row][new_col]]
                queue.append((new_row, new_col, new_path, length + 1))

    return []  # Should never reach here if a solution exists

